import React from "react";
import "../UserProfileRecipesListStyle.css";
import moment from "moment";
import { faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";

const UserProfileListRecipes = ({ recipes, toggleDeleteModal }) => {
  return (
    <>
      <h2 className="user_profile_list_title">Моите рецепти</h2>
      <table className="task">
        <thead>
          <tr>
            <th>Id</th>
            <th>Заглавие</th>
            <th>Дата</th>
            <th>Опции</th>
          </tr>
        </thead>
        <tbody>
          {recipes.map((recipe, index) => {
            return (
              <tr style={{ fontSize: 20, padding: 15 }} key={index}>
                <td> {recipe.id}</td>
                <td>
                  <img src={recipe.image} alt="task" />
                  {recipe.title}
                </td>
                <td>
                  {recipe.Date === null
                    ? ""
                    : moment.unix(recipe.Date).format("DD.MM.YYYY")}
                </td>
                <td>
                  <FontAwesomeIcon
                    className="delete"
                    onClick={() => toggleDeleteModal(recipe.id)}
                    icon={faTrash}
                  />
                  <Link to={`/my_profile/recipe/${recipe.title}`}>
                    <FontAwesomeIcon className="edit" icon={faEdit} />{" "}
                  </Link>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </>
  );
};

export default UserProfileListRecipes;
