import React from "react";
import "./FilteredCategoryListStyle.css";
import moment from "moment";
import { Link } from "react-router-dom";
import { SmallHorizontalCard } from "../../../../packages/card/Card";
import { H1 } from "../../../../packages/text/Text";

const FilteredCategoryList = ({ recipes, categories }) => {
  return (
    <div className="page_wrapper">
      <div className="recipes_list_wrapper">
        <H1
          h1={` Рецепти от категория ${(<span>{categories[0].name}</span>)}`}
        />
        {recipes.map((recipe, index) => {
          let date = moment.unix(recipe.Date).format("DD.MM.YYYY");
          return (
            <SmallHorizontalCard key={index} date={date} recipe={recipe} />
          );
        })}
      </div>
    </div>
  );
};
export default FilteredCategoryList;
