import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { withRouter } from "react-router-dom";
import RecipePreview from "./components/RecipePreview";
import UserContext from "../../../utils/UserContext";

const RecipePreviewContainer = (props) => {
  const [{ user }] = useContext(UserContext);
  const [recipe, setRecipe] = useState({});
  const [isLoading, setLoading] = useState(true);
  const [commentDescr, setComment] = useState("");
  useEffect(() => {
    getRecipe();
  }, []);

  const getRecipe = () => {
    setLoading(true);
    axios
      .get(`http://localhost:4000/tasks/${props.match.params.title}`)
      .then((res) => {
        setRecipe(res.data[0]);
        setLoading(false);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };
  const handleInput = (e) => {
    setComment(e.target.value);
  };
  const postComment = () => {
    let comment = {
      username: user.username,
      avatar: user.image,
      recipeId: recipe.id,
      comment: commentDescr,
    };
    axios
      .post(`http://localhost:4000/comments/`, { comment })
      .then((res) => {})
      .catch((err) => {
        console.log(err.response);
      });
  };
  if (isLoading) return null;

  return (
    <RecipePreview
      handleInput={handleInput}
      commentDescr={commentDescr}
      postComment={postComment}
      recipe={recipe}
    />
  );
};

export default withRouter(RecipePreviewContainer);
