import React, { useEffect, useState } from "react";
import axios from "axios";
import { withRouter } from "react-router-dom";
import FilteredCategoryList from "./components/FilteredCategoryList";

const FilteredCategoryListContainer = (props) => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setisLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [categoriesLoading, setCategoriesLoading] = useState(true);

  useEffect(() => {
      fetchCategories();
    fetchRecipes();

  }, [props.match.params.name]);


  const fetchCategories = () => {
    setCategoriesLoading(true);
    axios
      .get(`http://localhost:4000/category`)
      .then((res) => {
        const filteredCategories = res.data.filter(
          (category) => category.id === props.match.params.id
        );
        setCategories(filteredCategories);
        setCategoriesLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const fetchRecipes = () => {
        setisLoading(true);
        axios
            .get(`http://localhost:4000/tasks`)
            .then((res) => {
                const filteredRecipes = res.data.filter(
                    (recipe) => recipe.categoryId === props.match.params.id
                );
                setRecipes(filteredRecipes);
                setisLoading(false);
            })
            .catch((e) => console.error(e));
    };
  if (isLoading || categoriesLoading) return null;
  return <FilteredCategoryList categories={categories} recipes={recipes} />;
};

FilteredCategoryListContainer.propTypes = {};

export default withRouter(FilteredCategoryListContainer);
