import React, { useEffect, useState } from "react";
import axios from "axios";
import "./LowCarbsRecipesStyle.css";
import OrangeButton from "../../../../packages/buttons/orangeButton/OrangeButton";

const LowCarbsRecipes = (props) => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setisLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [categoriesLoading, setCategoriesLoading] = useState(true);

  useEffect(() => {
    fetchRecipes();
    fetchCategories();
  }, []);

  const fetchRecipes = () => {
    setisLoading(true);
    axios
      .get(`http://localhost:4000/tasks`)
      .then((res) => {
        setRecipes(res.data);
        setisLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const fetchCategories = () => {
    setCategoriesLoading(true);
    axios
      .get(`http://localhost:4000/category`)
      .then((res) => {
        setCategories(res.data);
        setCategoriesLoading(false);
      })
      .catch((e) => console.error(e));
  };

  const filteredRecipes = recipes.filter(
    (recipe) => recipe.categoryId === "6d787708-4697-4387-a4d9-88f8afafad81"
  );
  {
    console.log(recipes);
  }
  if (isLoading || categoriesLoading) return <div>...Loading</div>;

  return (
    <div className={"low_carbs_recipes"}>
      <div className="category">Нисковъглехидратни</div>
      <h2>Нисковъглехидратни рецепти за всеки вкус</h2>
      <div className="low_carbs_recipes_wrapper">
        <div className="low_carbs_main_recipe">
          <img
            src={filteredRecipes[0] && filteredRecipes[0].image}
            alt="low-carbs"
          />
          <div className="low_carbs_main_recipe_layout">
            <h3>{filteredRecipes[0] && filteredRecipes[0].title}</h3>
            <div
              className={"low_carbs_main_description"}
              dangerouslySetInnerHTML={{
                __html: filteredRecipes[0] && filteredRecipes[0].description,
              }}
            />{" "}
            <OrangeButton text={"Виж още"} linkTo={"/"} />{" "}
          </div>
        </div>
        <div className="low_carbs_sub_recipes">
          {filteredRecipes.slice(1, 3).map((recipe, index) => {
            return (
              <div key={index} className="low_carbs_sub_recipe">
                <img src={recipe.image && recipe.image} alt="ow-carbs" />
                <div className="low_carbs_sub_recipe_layout">
                  <h3>{recipe.title}</h3>
                  <div
                    className={"low_carbs_sub_description"}
                    dangerouslySetInnerHTML={{
                      __html: recipe.description,
                    }}
                  />{" "}
                  <OrangeButton text={"Виж още"} linkTo={"/"} />{" "}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default LowCarbsRecipes;
