import React from "react";
import axios from "axios";
import { Modal } from "react-bootstrap";
import ModalHeader from "react-bootstrap/ModalHeader";
import "./CreateRecipeStyle.css";
import TaskImageUploader from "../../tasks/taskImageUploader/TaskImageUploader";
import TextEditor from "../../tasks/textEditor/TextEditor";

class CreateRecipeContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      Date: "",
      id: Math.random() * 1000,
      errorTitle: "",
      description: "",
      file: "",
      imagePreviewUrl: "",
      categories: [],
      selectedCategory: "",
    };
  }
  componentDidMount = () => {
    this.fetchTasks();
  };
  fetchTasks = () => {
    axios.get(`http://localhost:4000/category`).then((res) => {
      this.setState({ categories: res.data });
    });
  };
  validateForm = () => {
    let isValid = true;
    if (this.state.title.length < 1 || this.state.title.length > 200) {
      isValid = false;
      this.setState({ errorTitle: "Please, enter valid title." });
    }

    return isValid;
  };
  resetForm = () => {
    this.setState({ errorTitle: "" });
  };
  handleSubmit = () => {
    let isCreateTaskFormValid = this.validateForm();

    if (!isCreateTaskFormValid) {
      return;
    }

    this.resetForm();
    let task = {
      id: this.state.id,
      title: this.state.title,
      Date: new Date(this.state.Date).getTime() / 1000,
      description:
        this.state.description !== "" ? this.state.description : undefined,
      image: this.state.imagePreviewUrl,
      categoryId: this.state.selectedCategory,
    };
    axios
      .post(`http://localhost:4000/tasks`, { task })
      .then((res) => {
        window.location.reload();
      })
      .catch((e) => console.error(e));
  };
  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  handleImageChange = (e) => {
    e.preventDefault();

    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result,
      });
    };

    reader.readAsDataURL(file);
  };
  handleQuillChange(value) {
    this.setState({ description: value });
  }
  handleSelect = (e) => {
    this.setState({ selectedCategory: e.target.value });
  };
  render() {
    const { title, Date, errorTitle, description } = this.state;
    const { isModalOpen, closeModal } = this.props;
    if (this.state.isLoading === true) return <p>Loading</p>;
    return (
      <Modal show={isModalOpen} onHide={closeModal}>
        <ModalHeader>
          <h2>Create task</h2>
          <span onClick={closeModal}>X</span>
        </ModalHeader>
        <div className="modal_content_holder">
          <label>Title</label>
          <TaskImageUploader
            handleImageChange={this.handleImageChange}
            imagePreviewUrl={this.state.imagePreviewUrl}
          />
          <input
            onChange={this.handleChange}
            name="title"
            type="text"
            placeholder="Enter title of the task"
            value={title}
          />
          {errorTitle && <p className="error">{errorTitle}</p>}
          <label>Date</label>
          <input
            onChange={this.handleChange}
            name="Date"
            placeholder="Enter date of the task"
            type="date"
            value={Date}
          />
          <label>Description</label>
          <TextEditor
            placeholder={"Започнете да пишете..."}
            label={"Enter description"}
            value={description}
            onTextEditorChange={(text) => this.handleQuillChange(text)}
          />{" "}
          <select
            onChange={this.handleSelect}
            value={this.state.selectedCategory}
          >
            <option value=""> Choose category</option>
            {this.state.categories.map((category, index) => {
              return (
                <option key={index} value={category.id}>
                  {category.name}
                </option>
              );
            })}
          </select>
          {console.log(this.state.selectedCategory)}
          <button onClick={this.handleSubmit}>Submit</button>
        </div>
      </Modal>
    );
  }
}

export default CreateRecipeContainer;
