import React from "react";
import "./ContactsStyle.css";
import OrangeButton from "../../../../packages/buttons/orangeButton/OrangeButton";

const Contacts = ({
  formState,
  handleInput,
  handleSubmit,
  successMessage,
  formStateError,
}) => {
  return (
    <section className="contacts_section">
      <img src={"/contacts.png"} alt="about-us" />
      <div className="contacts_section_content">
        <h1>Свържете се с нас</h1>
        <div className="contact_form">
          <div className="row">
            <div className="col-6">
              <label className={"contacts_label"}>Име</label>
              <input
                onChange={handleInput}
                name={"name"}
                value={formState.name}
                className={"contacts_input"}
                type={"text"}
              />
              {formStateError.name && (
                <p className="error">{formStateError.name}</p>
              )}
            </div>
            <div className="col-6">
              <label className={"contacts_label"}>Фамилия</label>
              <input
                className={"contacts_input"}
                type={"text"}
                name={"surname"}
                onChange={handleInput}
                value={formState.surname}
              />
              {formStateError.surname && (
                  <p className="error">{formStateError.surname}</p>
              )}
            </div>
          </div>{" "}
          <div className="row">
            <div className="col-6">
              <label className={"contacts_label"}>Имейл</label>
              <input
                className={"contacts_input"}
                type={"text"}
                onChange={handleInput}
                name={"email"}
                value={formState.email}
              />
              {formStateError.email && (
                  <p className="error">{formStateError.email}</p>
              )}
            </div>
            <div className="col-6">
              <label className={"contacts_label"}>Телефон</label>
              <input
                className={"contacts_input"}
                type={"number"}
                name={"phone"}
                onChange={handleInput}
                value={formState.phone}
              />
              {formStateError.phone && (
                  <p className="error">{formStateError.phone}</p>
              )}
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <label className={"contacts_label"}>Съобщение</label>
              <textarea
                className={"contacts_input"}
                rows={5}
                name={"message"}
                onChange={handleInput}
                value={formState.message}
              />
              {formStateError.message && (
                  <p className="error">{formStateError.message}</p>
              )}
            </div>
          </div>
          <div className="btn_wrapper">
            <OrangeButton onClick={handleSubmit} text={"Изпрати"} />
          </div>
          <p className="success">{successMessage}</p>
        </div>
      </div>
    </section>
  );
};

export default Contacts;
