import React, { useContext, useState } from "react";
import axios from "axios";
import LoginForm from "./components/LoginForm";
import { SET_USER } from "../store/actionTypes/UserActionTypes";
import Context from "../../../utils/UserContext";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
const LoginContainer = (props) => {
  const [userInfo, setUserInfo] = useState({
    password: "",
    email: "",
  });
  const [error, setError] = useState("");

  const [{ user }, dispatch] = useContext(Context);
  const auth = getAuth();
  const handleSubmit = () => {
    let user = {
      password: userInfo.password,
      email: userInfo.email,
    };
    axios
      .post(`http://localhost:4000/user/login`, { user })
      .then((res) => {
        window.location.assign("/");
        setUser(res.data[0]);
      })
      .catch((err) => {
        setError(err.response.data.message);
        console.log(err.response);
      });
  };
  const handleChange = (e) => {
    setUserInfo({ ...userInfo, [e.target.name]: e.target.value });
  };
  const sigIn = () => {
    signInWithEmailAndPassword(auth, userInfo.email, userInfo.password).then(
      (userCredential) => {
        {
          console.log(userCredential);
        }
      }
    );
  };
  const setUser = (user) => {
    dispatch({
      type: SET_USER,
      payload: { user },
    });
  };
  sigIn();
  return (
    <LoginForm
      error={error}
      handleChange={handleChange}
      handleSubmit={handleSubmit}
      password={userInfo.password}
      email={userInfo.email}
    />
  );
};

export default LoginContainer;
