import React from "react";

const TaskImageUploader = ({ handleImageChange, imagePreviewUrl }) => {
  return (
    <div className="previewComponent">
      <div className="image_uploader_wrapper">
        <input
          className="fileInput"
          type="file"
          onChange={(e) => handleImageChange(e)}
        />
      </div>
      {imagePreviewUrl ? (
        <img src={imagePreviewUrl} />
      ) : (
        <div>please select an image</div>
      )}
    </div>
  );
};

export default TaskImageUploader;
