import React, { useEffect, useState } from "react";
import "./RecipesStyle.css";
import Slider from "react-slick";
import axios from "axios";
import { Link } from "react-router-dom";
import { SmallCard } from "../../../../packages/card/Card";

const NewestRecipes = (props) => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setisLoading] = useState(false);
  const [isCategoriesLoading, setCategoriesLoading] = useState(false);
  const [categories, setCategories] = useState(false);

  useEffect(() => {
    fetchRecipes();
    fetchCategories();
  }, []);

  const fetchRecipes = () => {
    setisLoading(true);
    axios
      .get(`http://localhost:4000/recipes`)
      .then((res) => {
        setRecipes(res.data);
        setisLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const fetchCategories = () => {
    setCategoriesLoading(true);
    axios
      .get(`http://localhost:4000/category`)
      .then((res) => {
        setCategories(res.data);
        setCategoriesLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
  };
  if (isLoading || isCategoriesLoading) return <div>...</div>;
  return (
    <section className="newest_recipes_section">
      <h2>Най-новите ни рецепти</h2>
      <Slider className="newest_slider" {...settings}>
        {recipes
          .sort((a, b) => a.Date - b.Date)
          .map((recipe, index) => {
            let category = categories.filter(
              (category) => category.id === recipe.categoryId
            );
            return (
              <SmallCard key={index} recipe={recipe} category={category} />
            );
          })}
      </Slider>
    </section>
  );
};

export default NewestRecipes;
