import React, { useEffect, useState } from "react";
import axios from "axios";
import "./CreateRecipeStyle.css";
import TaskImageUploader from "../../tasks/taskImageUploader/TaskImageUploader";
import TextEditor from "../../tasks/textEditor/TextEditor";
import UserLayout from "../../../packages/userLayout/UserLayout";
import OrangeButton from "../../../packages/buttons/orangeButton/OrangeButton";

const CreateRecipeContainer = (props) => {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [id, setId] = useState(Math.random() * 1000);
  const [errorTitle, setErrorTitle] = useState("");
  const [description, setDescription] = useState("");
  const [file, setFile] = useState("");
  const [imagePreviewUrl, setImagePreviewUrl] = useState("");
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [categoriesLoading, setCategoriesLoading] = useState(true);

  useEffect(() => {
    fetchCategories();
  }, []);
  const fetchCategories = () => {
    setCategoriesLoading(true);
    axios
      .get(`http://localhost:4000/category`)
      .then((res) => {
        setCategories(res.data);
        setCategoriesLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const validateForm = () => {
    let isValid = true;
    if (title.length < 1 || title.length > 200) {
      isValid = false;
      setErrorTitle("Моля въведете валидно заглавие");
    }

    return isValid;
  };
  const resetForm = () => {
    setErrorTitle("");
  };
  const handleSubmit = () => {
    let isCreateTaskFormValid = validateForm();

    if (!isCreateTaskFormValid) {
      return;
    }

    resetForm();
    let recipe = {
      id: id,
      title: title,
      Date: new Date(date).getTime() / 1000,
      description: description !== "" ? description : undefined,
      image: imagePreviewUrl,
      categoryId: selectedCategory,
    };
    axios
      .post(`http://localhost:4000/tasks`, { recipe })
      .then((res) => {
        window.location.reload();
      })
      .catch((e) => console.error(e));
  };
  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };
  const handleDateChange = (e) => {
    setDate(e.target.value);
  };
  const handleImageChange = (e) => {
    e.preventDefault();

    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      setFile(file);
      setImagePreviewUrl(reader.result);
    };

    reader.readAsDataURL(file);
  };
  const handleQuillChange = (value) => {
    setDescription(value);
  };
  const handleSelect = (e) => {
    setSelectedCategory(e.target.value);
  };
  if (categoriesLoading) return null;
  return (
    <div className="page_wrapper">
      <UserLayout>
        <h2 className="create_recipe_title"> Създай рецепта</h2>
        <label className="create_recipe_label">Заглавие</label>
        <TaskImageUploader
          handleImageChange={handleImageChange}
          imagePreviewUrl={imagePreviewUrl}
        />
        <input
          className="create_recipe_input"
          onChange={handleTitleChange}
          name="title"
          type="text"
          placeholder="Въведи заглавие"
          value={title}
        />
        {errorTitle && <p className="error">{errorTitle}</p>}
        <label className="create_recipe_label">Дата</label>
        <input
          className="create_recipe_input"
          onChange={handleDateChange}
          name="Date"
          placeholder="Въведи дата"
          type="date"
          value={date}
        />
        <label className="create_recipe_label">Описание</label>
        <TextEditor
          placeholder={"Започнете да пишете..."}
          value={description}
          onTextEditorChange={(text) => handleQuillChange(text)}
        />{" "}
        <label className="create_recipe_label">Изберете категория</label>
        <select onChange={handleSelect} value={selectedCategory}>
          <option value=""> Категория</option>
          {categories.map((category, index) => {
            return (
              <option key={index} value={category.id}>
                {category.name}
              </option>
            );
          })}
        </select>
        {console.log(selectedCategory)}
        <div className="create_recipe_submit_btn_holder">
          <OrangeButton onClick={handleSubmit} text={"Създай"} />
        </div>
      </UserLayout>
    </div>
  );
};

export default CreateRecipeContainer;
