import React, { useEffect, useState } from "react";
import "./RecipesStyle.css";
import Slider from "react-slick";
import axios from "axios";
import {Link} from "react-router-dom";

const NewestRecipes = (props) => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setisLoading] = useState(false);
  const [isCategoriesLoading, setCategoriesLoading] = useState(false);
  const [categories, setCategories] = useState(false);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = () => {
    setisLoading(true);
    axios
      .get(`http://localhost:4000/tasks`)
      .then((res) => {
        setRecipes(res.data);
        setisLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const fetchCategories = () => {
    setCategoriesLoading(true);
    axios
        .get(`http://localhost:4000/category`)
        .then((res) => {
          // const filteredCategories = res.data.filter(
          //     (category) => category.id === id
          // );
          setCategories(res.data);
          setCategoriesLoading(false);
        })
        .catch((e) => console.error(e));
  };
  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
  };
  if (isLoading || isCategoriesLoading) return <div>...</div>;
  return (
    <section className="newest_recipes_section">
      <h2>Най-новите ни рецепти</h2>
      <Slider className="newest_slider" {...settings}>
        {recipes
          .sort((a, b) => a.Date - b.Date)
          .map((recipe, index) => {
            let category =   categories.filter(
                 (category) => category.id === recipe.categoryId
             );
            return (
              <Link to={`/рецепта/${recipe.title}`} key={index} className="newest_recipe_card">
                <div>{category[0].name}</div>
                <div className="newest_recipe_card_image_holder">
                  <img src={recipe.image} alt="card" />
                </div>
                <h3>{recipe.title}</h3>
              </Link>
            );
          })}
      </Slider>
    </section>
  );
};

export default NewestRecipes;
