import React, { useEffect, useState } from "react";
import "./MostPopularRecipesStyle.css";
import Slider from "react-slick";
import axios from "axios";

const MostPopularRecipes = (props) => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setisLoading] = useState(true);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = () => {
    setisLoading(true);
    axios
      .get(`http://localhost:4000/tasks`)
      .then((res) => {
        setRecipes(res.data);
        setisLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const settings = {
    dots: true,
    infinite: false,
    arrows: false,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
  };
  if (isLoading) return <div>...</div>;
  let newRecipes = recipes.sort(() => Math.random() - 0.5);

  return (
    <section className="most_popular_recipes">
      <h2>Най-популярните ни рецепти</h2>{" "}
      <Slider className="most_popular_slider" {...settings}>
        {newRecipes.map((recipe, index) => {
          return (
            <div key={index} className="most_popular_recipe_card">
              <div className="most_popular_recipe_card_image_holder">
                <img src={recipe.image} alt="card" />
              </div>
              <h3>{recipe.title}</h3>
            </div>
          );
        })}
      </Slider>
    </section>
  );
};

export default MostPopularRecipes;
