import React, { useContext, useState } from "react";
import "./UserLayoutStyle.css";
import { Link } from "react-router-dom";
import UserContext from "../../utils/UserContext";
import { SET_USER } from "../../modules/user/store/actionTypes/UserActionTypes";

const UserLayout = ({ children }) => {
  const [{ user }, dispatch] = useContext(UserContext);
  const [isAdminPanel, setAdminPanel] = useState(false);

  const setUser = (user) => {
    dispatch({
      type: SET_USER,
      payload: { user },
    });
  };
  const goToAdminPanel = () => {
    setAdminPanel(true);
    window.location.href = "/my_profile/recipes";
  };
  return (
    <div className="user_layout">
      <div className="user_layout_sidebar">
        <h3>Навигация</h3>
        <Link className="user_sidebar_option" to={"/my_profile"}>
          Моят профил
        </Link>
        <div className="user_sidebar_option" onClick={() => setUser({})}>
          Изход
        </div>
        {user.username && user.role === "ADMIN" && isAdminPanel === true ? (
          <>
            <div className="user_recipes_holder">
              <h3>Рецепти</h3>
              <Link to={"/my_profile/recipes"} className="user_sidebar_option">
                Моите рецепти
              </Link>
              <Link
                className="user_sidebar_option"
                to={"/my_profile/recipe/create"}
              >
                Създай рецепта
              </Link>
            </div>
            <div className="user_recipes_holder">
              <h3>Категории</h3>{" "}
              <Link to="/my_profile/categories" className="user_sidebar_option">
                Моите категории
              </Link>
              <Link
                className="user_sidebar_option"
                to={"/my_profile/category/create"}
              >
                Създай категория
              </Link>
            </div>
            <div className="user_recipes_holder">
              <h3>Абонаменти</h3>{" "}
              <Link to="/my_profile/emails" className="user_sidebar_option">
                Имейли
              </Link>
            </div>
          </>
        ) : null}
      </div>

      <div className="user_content_layout">
        {children}{" "}
        {user.username && user.role === "ADMIN" && (
          <div onClick={goToAdminPanel} className="user_sidebar_admin_btn">
            Към администрацията
          </div>
        )}
      </div>
    </div>
  );
};

export default UserLayout;
