import React, { useEffect, useState } from "react";
import "./Header.css";
import { Link, NavLink } from "react-router-dom";
import LoginContainer from "../../../modules/user/login/LoginContainer";

const Header = (props) => {
  const [scrolling, setScrolling] = useState(false);
  const [scrollPosition, setScrollTop] = useState(0);
  const [toggleModal, setToggleModal] = useState(false);
  useEffect(() => {
    const onScroll = (e) => {
      setScrollTop(e.target.documentElement.scrollTop);
      setScrolling(e.target.documentElement.scrollTop > scrollPosition);
    };
    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [scrollPosition]);
  const toggleLoginModal = () => {
    setToggleModal(true);
  };
  return (
    <>
      <header className={scrollPosition > 120 ? "scrolled_header" : "header"}>
        <div className="header_content">
          <Link to="/" className="logo_holder">
            <img src={"/logo.svg"} alt="logo" />
          </Link>
          <div className="header_nav_menu">
            <NavLink
              exact
              className="nav_link"
              to={"/"}
              activeClassName={"active_link"}
            >
              Начало
            </NavLink>
            <NavLink
              exact
              className="nav_link"
              activeClassName={"active_link"}
              to={"/recipes"}
            >
              Рецепти
            </NavLink>
            <NavLink
              exact
              className="nav_link"
              activeClassName={"active_link"}
              to={"/about-us"}
            >
              За нас
            </NavLink>
            <NavLink
              exact
              className="nav_link"
              activeClassName={"active_link"}
              to={"/contacts"}
            >
              Контакти
            </NavLink>
            <Link
              to={"/login"}
              className={
                scrollPosition > 120 && window.location.pathname === "/"
                  ? "orange_login_btn"
                  : window.location.pathname !== "/"
                  ? "orange_login_btn"
                  : "login_btn"
              }
            >
              Вход
            </Link>
          </div>
        </div>
      </header>
      {toggleModal && <LoginContainer setToggleModal={setToggleModal} />}
    </>
  );
};

export default Header;
