import React, { useState } from "react";
import Contacts from "./components/Contacts";
import axios from "axios";
import emailValidator from "../../utils/EmailValidator";

const ContactsContainer = (props) => {
  const [formState, setFormState] = useState({
    name: "",
    surname: "",
    email: "",
    phone: null,
    message: "",
  });
  const [formStateError, setFormStateError] = useState({
    name: "",
    surname: "",
    email: "",
    phone: null,
    message: "",
  });
  const [successMessage, setSuccessMessage] = useState("");
  const handleInput = (e) => {
    setFormState({ ...formState, [e.target.name]: e.target.value });
  };
  const validateForm = () => {
    let isValid = true;
    if (formState.name.length || formState.name.length < 1) {
      isValid = false;
      setFormStateError({
        ...formStateError,
        name: "Моля въведете вашето име",
      });
    }
    if (formState.surname.length || formState.surname.length < 1) {
      isValid = false;
      setFormStateError({
        ...formStateError,
        surname: "Моля въведете вашата фамилия",
      });
    }
    if (
      formState.email.length ||
      formState.email.length < 1 ||
      !emailValidator(formState.email)
    ) {
      isValid = false;
      setFormStateError({
        ...formStateError,
        email: "Моля въведете валиден имейл",
      });
    }
    if (formState.phone || formState.phone < 1 || formState.phone > 10) {
      isValid = false;
      setFormStateError({
        ...formStateError,
        phone: "Моля въведете вашия телефонен номер",
      });
    }
    if (formState.message.length || formState.message.length < 1) {
      isValid = false;
      setFormStateError({
        ...formStateError,
        message: "Моля въведете вашия коментар",
      });
    }

    return isValid;
  };
  const resetForm = () => {
    setFormStateError({
      name: "",
      surname: "",
      email: "",
      phone: "",
      message: "",
    });
  };
  const handleSubmit = () => {
    let isContactFormValid = validateForm();

    if (!isContactFormValid) {
      return;
    }

    resetForm();
    let contact = {
      name: formState.name,
      surname: formState.surname,
      email: formState.email,
      phone: formState.phone,
      message: formState.message,
    };
    axios
      .post(`http://localhost:4000/contacts`, { contact })
      .then((res) => {
        setSuccessMessage(
          "Вие успешно изпратихте вашето запитване! Ще изпратим нашия отговор, възможно най-бързо, към вашия имейл!"
        );
      })
      .catch((e) => console.error(e));
  };
  return (
    <Contacts
      handleSubmit={handleSubmit}
      formState={formState}
      successMessage={successMessage}
      formStateError={formStateError}
      handleInput={handleInput}
    />
  );
};

export default ContactsContainer;
