import React, { useContext, useEffect, useState } from "react";
import "./Header.css";
import { Link, NavLink } from "react-router-dom";
import UserContext from "../../../utils/UserContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserAlt } from "@fortawesome/free-solid-svg-icons";
import { SET_USER } from "../../../modules/user/store/actionTypes/UserActionTypes";

const Header = (props) => {
  const [scrolling, setScrolling] = useState(false);
  const [scrollPosition, setScrollTop] = useState(0);
  const [{ user }, dispatch] = useContext(UserContext);

  useEffect(() => {
    const onScroll = (e) => {
      setScrollTop(e.target.documentElement.scrollTop);
      setScrolling(e.target.documentElement.scrollTop > scrollPosition);
    };
    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [scrollPosition, user]);

  const setUser = (user) => {
    dispatch({
      type: SET_USER,
      payload: { user },
    });
  };

  return (
    <header className={scrollPosition > 120 ? "scrolled_header" : "header"}>
      <div className="header_content">
        <Link to="/" className="logo_holder">
          <img src={"/logo.svg"} alt="logo" />
        </Link>
        <div className="header_nav_menu">
          <NavLink
            exact
            className="nav_link"
            to={"/"}
            activeClassName={"active_link"}
          >
            Начало
          </NavLink>
          <NavLink
            exact
            className="nav_link"
            activeClassName={"active_link"}
            to={"/recipes"}
          >
            Рецепти
          </NavLink>
          <NavLink
            exact
            className="nav_link"
            activeClassName={"active_link"}
            to={"/about-us"}
          >
            За нас
          </NavLink>
          <NavLink
            exact
            className="nav_link"
            activeClassName={"active_link"}
            to={"/contacts"}
          >
            Контакти
          </NavLink>
          {user.username ? (
            <div className="user_header_box">
              {" "}
              <div className="user">
                <FontAwesomeIcon icon={faUserAlt} /> {user.username}
              </div>
              <div className="user_dropdown">
                <div
                  className={
                    scrollPosition > 120 && window.location.pathname === "/"
                      ? "user_white_dropdown_content"
                      : "user_dropdown_content"
                  }
                >
                  <Link to={"/user"} className={"user_dropdown_option"}>
                    Към профила
                  </Link>
                  <div
                    onClick={() => setUser({})}
                    className={"user_dropdown_option"}
                  >
                    Изход
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <Link
              to={"/login"}
              className={
                scrollPosition > 120 && window.location.pathname === "/"
                  ? "orange_login_btn"
                  : window.location.pathname !== "/"
                  ? "orange_login_btn"
                  : "login_btn"
              }
            >
              Вход
            </Link>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
