import React, { useEffect, useState } from "react";
import RecipesList from "./components/RecipesList";
import axios from "axios";

const RecipesListContainer = (props) => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [recipeToShow, setRecipesToShow] = useState(8);
  const [categories, setCategories] = useState([]);
  const [categoriesLoading, setCategoriesLoading] = useState(true);

  useEffect(() => {
    fetchRecipes();
    fetchCategories();
  }, []);

  const fetchRecipes = () => {
    setIsLoading(true);
    axios
      .get(`http://localhost:4000/tasks`)
      .then((res) => {
        setRecipes(res.data);
        setIsLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const fetchCategories = () => {
    setCategoriesLoading(true);
    axios
      .get(`http://localhost:4000/category`)
      .then((res) => {
        setCategories(res.data);
        setCategoriesLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const showMore = () => {
    setRecipesToShow(recipeToShow + 8);
  };
  if (isLoading || categoriesLoading) return <p>Loading</p>;
  return (
    <RecipesList
      showMore={showMore}
      categories={categories}
      recipesToShow={recipeToShow}
      recipes={recipes}
    />
  );
};

export default RecipesListContainer;
