import React from "react";
import "./RecipesListStyle.css";
import OrangeButton from "../../../../packages/buttons/orangeButton/OrangeButton";
import { Link } from "react-router-dom";
import moment from "moment";
const RecipesList = ({ recipes, showMore, recipesToShow, categories }) => {
  return (
    <div className="page_wrapper">
      <div className="recipes_list_wrapper">
        <h1 className="main_title">Всички рецепти</h1>
        {recipes.slice(0, recipesToShow).map((recipe, index) => {
          let date = moment.unix(recipe.Date).format("DD.MM.YYYY");
          let filteredCategory = categories.filter((category) => {
            console.log(category.id);
          });
          {
            console.log(recipe);
          }
          return (
            <div className="recipe_holder">
              <Link
                to={`/recipes/${recipe.title}`}
                className="recipe_image_holder"
              >
                <img src={recipe.image} alt={"image"} />
              </Link>
              <div className="recipe_content">
                <Link to={`/category/${filteredCategory.id}`}>
                  {filteredCategory.name}
                </Link>
                <Link to={`/recipes/${recipe.title}`}>{recipe.title}</Link>
                <div className="recipe_date">{date}</div>
                <div
                  className="recipe_description"
                  dangerouslySetInnerHTML={{
                    __html: recipe.description,
                  }}
                />
              </div>
            </div>
          );
        })}
        {recipesToShow < recipes.length && (
          <div className="recipe_btn_holder">
            <OrangeButton onClick={showMore} text={"Покажи още"} />
          </div>
        )}
      </div>
    </div>
  );
};

export default RecipesList;
