import React from "react";
import "./FilteredCategoryListStyle.css";
import moment from "moment";
import { Link } from "react-router-dom";

const FilteredCategoryList = ({ recipes, categories }) => {
  return (
    <div className="page_wrapper">
      <div className="recipes_list_wrapper">
        <h1 className="main_title">
          Рецепти от категория <span>{categories[0].name}</span>
        </h1>
        {recipes.map((recipe, index) => {
          let date = moment.unix(recipe.Date).format("DD.MM.YYYY");
          return (
            <div key={index} className="recipe_holder">
              <Link
                to={`/рецепта/${recipe.title}`}
                className="recipe_image_holder"
              >
                <img src={recipe.image} alt={"image"} />
              </Link>
              <div className="recipe_content">
                <Link to={`/рецепта/${recipe.title}`}>{recipe.title}</Link>
                <div className="recipe_date">{date}</div>
                <div
                  className="recipe_description"
                  dangerouslySetInnerHTML={{
                    __html: recipe.description,
                  }}
                />
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};
export default FilteredCategoryList;
