import React, { useEffect, useState } from "react";
import axios from "axios";
import CategoryList from "./components/CategoryList";
import UserLayout from "../../../packages/userLayout/UserLayout";
import ListRow from "../../../packages/listRow/ListRow";
import moment from "moment";

const CategoryListContainer = (props) => {
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    getCategories();
  }, []);

  const getCategories = () => {
    setIsLoading(true);
    axios
      .get(`http://localhost:4000/category/`)
      .then((res) => {
        setCategories(res.data);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };
  const tableCells = (cell) => {
    return (
      <>
        <td> {cell.id}</td>
        <td>
          <img src={cell.image} alt="task" />
          {cell.title}
        </td>
        <td>
          {cell.Date === null
            ? ""
            : moment.unix(cell.Date).format("DD.MM.YYYY")}
        </td>
      </>
    );
  };
  if (isLoading) return null;
  return (
    <div className="page_wrapper">
      <UserLayout>
        <ListRow
          dataList={categories}
          listRowTitle={"Моите категории"}
          isEditable={true}
          tableHeads={["Id", "Име", "Опции"]}
          tableCells={tableCells}
          editableLink={`/my_profile/category`}
        />
        <CategoryList categories={categories} />
      </UserLayout>
    </div>
  );
};

export default CategoryListContainer;
