import axios from "axios";
import React from "react";
import TaskEditContainer from "../taskEdit/TaskEditContainer";
import CreateTaskContainer from "../taskCreate/CreateTaskContainer";
import TaskList from "../../recipes/userProfileList/components/TaskList";
import "./TaskListstyle.css";
import DeleteModal from "../../recipes/userProfileList/components/DeleteModal";

class TasksListContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      tasks: [],
      isOpenModal: false,
      selectedTask: 0,
      isOpenCreateModal: false,
      isOpenDeleteModal: false,
      selectedTaskToDelete: 0,
    };
  }
  componentDidMount = () => {
    this.fetchTasks();
  };
  fetchTasks = () => {
    axios.get(`http://localhost:4000/tasks`).then((res) => {
      this.setState({ tasks: res.data });
    });
  };
  deleteTask = (id) => {
    axios.delete(`http://localhost:4000/tasks/${id}`).then((res) => {
      this.setState({ tasks: res.data });
    });
    window.location.reload();
  };
  toggleEditModal = (id) => {
    this.setState({ isOpenModal: !this.state.isOpenModal });
    this.setState({ selectedTask: id });
  };
  toggleCreateModal = () => {
    this.setState({ isOpenCreateModal: !this.state.isOpenCreateModal });
  };
  toggleDeleteModal = (id) => {
    this.setState({ isOpenDeleteModal: !this.state.isOpenDeleteModal });
    this.setState({ selectedTaskToDelete: id });
  };

  render() {
    const {
      isOpenModal,
      isOpenCreateModal,
      tasks,
      isOpenDeleteModal,
      selectedTaskToDelete,
    } = this.state;
    return (
      <div className="tasks_list">
        <div className="tasks_header">
          <h1>Tasks</h1>
          <button onClick={() => this.toggleCreateModal()}>Create</button>
        </div>
        <TaskList
          toggleDeleteModal={this.toggleDeleteModal}
          tasks={tasks}
          toggleEditModal={this.toggleEditModal}
        />
        {isOpenModal === true && (
          <TaskEditContainer
            isModalOpen={isOpenModal}
            closeModal={() => this.toggleEditModal(this.state.selectedTask)}
            id={this.state.selectedTask}
          />
        )}
        {isOpenCreateModal === true && (
          <CreateTaskContainer
            closeModal={() => this.toggleCreateModal()}
            isModalOpen={isOpenCreateModal}
          />
        )}
        {isOpenDeleteModal === true && (
          <DeleteModal
            isModalOpen={isOpenDeleteModal}
            closeModal={() => this.toggleDeleteModal(selectedTaskToDelete)}
            deleteTask={() => this.deleteTask(selectedTaskToDelete)}
          />
        )}
      </div>
    );
  }
}
export default TasksListContainer;
