import React, { useState } from "react";
import Contacts from "./components/Contacts";
import axios from "axios";

const ContactsContainer = (props) => {
  const [formState, setFormState] = useState({
    name: "",
    surname: "",
    email: "",
    phone: "",
    message: "",
  });
  const [formStateError, setFormStateError] = useState({
    name: "",
    surname: "",
    email: "",
    phone: "",
    message: "",
  });
  const handleInput = (e) => {
    setFormState({ [e.target.name]: e.target.value });
  };
  const validateForm = () => {
    let isValid = true;
    if (formState.name.length < 1) {
      isValid = false;
      setFormStateError({ name: "Моля въведете вашето име" });
    }

    return isValid;
  };
  const resetForm = () => {
    setFormStateError({
      name: "",
      surname: "",
      email: "",
      phone: "",
      message: "",
    });
  };
  const handleSubmit = () => {
    let isCreateTaskFormValid = validateForm();

    if (!isCreateTaskFormValid) {
      return;
    }

    resetForm();
    let recipe = {
      id: id,
      title: title,
      Date: new Date(date).getTime() / 1000,
      description: description !== "" ? description : undefined,
      image: imagePreviewUrl,
      categoryId: selectedCategory,
      cooking_time: cookingTime,
      level_of_difficulty: levelOfDifficulty,
    };
    axios
      .post(`http://localhost:4000/recipes`, { recipe })
      .then((res) => {
        window.location.reload();
      })
      .catch((e) => console.error(e));
  };
  return (
    <Contacts
      handleSubmit={handleSubmit}
      formState={formState}
      handleInput={handleInput}
    />
  );
};

export default ContactsContainer;
