import React, { useEffect, useState } from "react";
import axios from "axios";
import { withRouter } from "react-router-dom";

const FilteredCategoryListContainer = (props) => {
  const [recipes, setRecipes] = useState([]);
  const [isLoading, setisLoading] = useState(true);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = () => {
    setisLoading(true);
    axios
      .get(`http://localhost:4000/tasks`)
      .then((res) => {
        const filteredRecipes = res.data.filter(
          (recipe) => recipe.categoryId === props.match.params.id
        );
        setRecipes(filteredRecipes);
        setisLoading(false);
      })
      .catch((e) => console.error(e));
  };
  if (isLoading) return null;
  return <div>f{console.log(recipes)}</div>;
};

FilteredCategoryListContainer.propTypes = {};

export default withRouter(FilteredCategoryListContainer);
