import React, { useEffect, useState } from "react";
import UserLayout from "../../../packages/userLayout/UserLayout";
import DeleteModal from "../../recipes/userProfileList/components/DeleteModal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";

const ContactsList = (props) => {
  const [contacts, setContacts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isOpenDeleteModal, setIsOpenDeleteModal] = useState(false);
  const [selectedContactToDelete, setSelectedContactToDelete] = useState("");
  useEffect(() => {
    getContacts();
  }, []);

  const getContacts = () => {
    setIsLoading(true);
    axios
      .get(`http://localhost:4000/contacts`)
      .then((res) => {
        setContacts(res.data);
        setIsLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const deleteContact = (contact) => {
    axios.delete(`http://localhost:4000/contacts/${contact}`).then((res) => {
      window.location.reload();
    });
    window.location.reload();

  };

  const toggleDeleteModal = (contact) => {
    setIsOpenDeleteModal(!isOpenDeleteModal);
    setSelectedContactToDelete(contact);
  };
  if (isLoading) return <div>Loading...</div>;
  return (
    <div className="page_wrapper">
      <UserLayout>
        <h2 className="emails_list_title">Контакти</h2>
        <table className="email">
          <thead>
            <tr>
              <th>#</th>
              <th>Име</th>
              <th>Фамилия</th>
              <th>Телефон</th>
              <th>Имейл</th>
              <th>Опции</th>
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact, index) => {
              return (
                <tr style={{ fontSize: 20, padding: 15 }} key={index}>
                  <td> {index + 1}</td>
                  <td>{contact.name}</td>
                  <td>{contact.surname}</td>
                  <td>{contact.phone}</td>
                  <td>{contact.email}</td>
                  <td>
                    <FontAwesomeIcon
                      onClick={() => toggleDeleteModal(contact.id)}
                      className="delete"
                      icon={faTrash}
                    />{" "}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </UserLayout>
      {isOpenDeleteModal === true && (
        <DeleteModal
          isModalOpen={isOpenDeleteModal}
          closeModal={() => toggleDeleteModal(selectedContactToDelete)}
          deleteTask={() => deleteContact(selectedContactToDelete)}
        />
      )}
    </div>
  );
};

export default ContactsList;
