import React, { useEffect, useState } from "react";
import axios from "axios";
import "./EditRecipeStyle.css";
import moment from "moment";
import RecipeImageUploader from "../recipeImageUploader/RecipeImageUploader";
import TextEditor from "../textEditor/TextEditor";
import UserLayout from "../../../packages/userLayout/UserLayout";
import OrangeButton from "../../../packages/buttons/orangeButton/OrangeButton";
import { withRouter } from "react-router-dom";

const RecipeEditContainer = (props) => {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");
  const [file, setFile] = useState("");
  const [image, setImage] = useState("");
  const [categories, setCategories] = useState([]);
  const [categoryId, setCategoryId] = useState("");
  const [categoryName, setCategoryName] = useState("");
  const [categoriesLoading, setCategoriesLoading] = useState(true);
  const [isLoading, setIsLoading] = useState(true);
  const [categoryLoading, setCategoryLoading] = useState(true);
  const [cookingTime, setCookingTime] = useState("");
  const [levelOfDifficulty, setLevelOfDifficulty] = useState(0);

  useEffect(() => {
    fetchRecipe();
    fetchCategories();
    getCategoryById();
  }, []);

  const fetchRecipe = () => {
    setIsLoading(true);
    axios
      .get(`http://localhost:4000/tasks/${props.match.params.title}`)
      .then((res) => {
        setTitle(res.data[0].title);
        setDate(moment.unix(res.data[0].Date).format("YYYY-MM-DD"));
        setDescription(res.data[0].description);
        setImage(res.data[0].image);
        setCategoryId(res.data[0].categoryId);
        setLevelOfDifficulty(res[0].data.level_of_difficulty);
        setCookingTime(res[0].data.cooking_time);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };
  const fetchCategories = () => {
    setCategoriesLoading(true);
    axios
      .get(`http://localhost:4000/category`)
      .then((res) => {
        setCategories(res.data);
        setCategoriesLoading(false);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };
  const getCategoryById = () => {
    setCategoryLoading(true);
    axios
      .get(`http://localhost:4000/category/${categoryId}`)
      .then((res) => {
        setCategoryName(res.data[0].name);
        setCategoryLoading(false);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };

  const handleSubmit = () => {
    let recipe = {
      title: title,
      Date: new Date(date).getTime() / 1000,
      description: description,
      image: image,
      categoryId: categoryId,
      level_of_difficulty: levelOfDifficulty,
      cooking_time: cookingTime,
    };

    axios
      .put(`http://localhost:4000/tasks/${props.match.params.title}`, {
        recipe,
      })
      .then((res) => {
        window.location.reload();
      })
      .catch((err) => {
        console.log(err.response);
      });
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };
  const handleDateChange = (e) => {
    setDate(e.target.value);
  };
  const handleImageChange = (e) => {
    e.preventDefault();

    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      setFile(file);
      setImage(reader.result);
    };

    reader.readAsDataURL(file);
  };
  const handleQuillChange = (value) => {
    setDescription(value);
  };
  const handleSelect = (e) => {
    setCategoryId(e.target.value);
  };
  const handleCookingTimeChange = (e) => {
    setCookingTime(e.target.value);
  };
  const handleLevelOfDifficultyChange = (e) => {
    setLevelOfDifficulty(e.target.value);
  };
  if (categoriesLoading || isLoading || categoryLoading) return null;
  return (
    <div className="page_wrapper">
      <UserLayout>
        <h2 className="edit_recipe_title">Редактирай рецепта с име: {title}</h2>{" "}
        <RecipeImageUploader
          handleImageChange={handleImageChange}
          imagePreviewUrl={image}
        />
        <div>
          <label className="edit_recipe_label">Заглавие</label>{" "}
          <input
            className="edit_recipe_input"
            onChange={handleTitleChange}
            name="title"
            type="text"
            value={title}
          />
          <label className="edit_recipe_label">Дата</label>{" "}
          <input
            className="edit_recipe_input"
            onChange={handleDateChange}
            name="date"
            type="text"
            value={date}
          />
          <label className="create_recipe_label">Време за приготване</label>
          <input
            className="create_recipe_input"
            onChange={handleCookingTimeChange}
            name="cookingTime"
            type="text"
            value={cookingTime}
          />
          <label className="create_recipe_label">Трудност</label>
          <input
            className="create_recipe_input"
            onChange={handleLevelOfDifficultyChange}
            name="levelOfDifficulty"
            type="text"
            value={levelOfDifficulty}
          />
          <label className="edit_recipe_label">Описание</label>
          <TextEditor
            placeholder={"Започнете да пишете..."}
            value={description}
            onTextEditorChange={(text) => handleQuillChange(text)}
          />{" "}
          <label className={"edit_recipe_label"}>Изберете категория</label>
          <select
            className="edit_recipe_input"
            onChange={handleSelect}
            value={categoryId}
          >
            <option value=""> Choose category</option>
            {categories.map((category, index) => {
              return (
                <option key={index} value={category.id}>
                  {category.name}
                </option>
              );
            })}
          </select>
          <div className="edit_recipe_submit_btn_holder">
            <OrangeButton onClick={handleSubmit} text={"Редактирай"} />
          </div>
        </div>
      </UserLayout>
    </div>
  );
};

export default withRouter(RecipeEditContainer);
