import axios from "axios";
import React, { useEffect, useState } from "react";
import TaskEditContainer from "../editRecipe/TaskEditContainer";
import UserProfileListRecipes from "./components/UserProfileListRecipes";
import "./UserProfileRecipesListStyle.css";
import DeleteModal from "./components/DeleteModal";
import UserLayout from "../../../packages/userLayout/UserLayout";

const UserProfileRecipesListContainer = (props) => {
  const [recipes, setRecipes] = useState([]);
  const [isOpenEditModal, setIsOpenEditModal] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(0);
  const [isOpenDeleteModal, setIsOpenDeleteModal] = useState(false);
  const [selectedTaskToDelete, setSelectedTaskToDelete] = useState(0);
  const [isLoading, setLoading] = useState(true);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = () => {
    setLoading(true);
    axios
      .get(`http://localhost:4000/tasks`)
      .then((res) => {
        setRecipes(res.data);
        setLoading(false);
      })
      .catch((e) => console.error(e));
  };
  const deleteTask = (id) => {
    axios.delete(`http://localhost:4000/tasks/${id}`).then((res) => {
      setRecipes(res.data);
    });
    window.location.reload();
  };
  // const toggleEditModal = (id) => {
  //   this.setState({ isOpenModal: !this.state.isOpenModal });
  //   this.setState({ selectedTask: id });
  // };

  const toggleDeleteModal = (id) => {
    setIsOpenDeleteModal(!isOpenDeleteModal);
    setSelectedTaskToDelete(id);
  };
  if (isLoading) return null;
  return (
    <div className="page_wrapper">
      <UserLayout>
        {/*<button onClick={() => this.toggleCreateModal()}>Create</button>*/}
        <UserProfileListRecipes
          toggleDeleteModal={toggleDeleteModal}
          recipes={recipes}
          //toggleEditModal={toggleEditModal}
        />
      </UserLayout>

      {/*{isOpenEditModal && (*/}
      {/*  <TaskEditContainer*/}
      {/*    isModalOpen={isOpenEditModal}*/}
      {/*    closeModal={() => toggleEditModal(selectedRecipe)}*/}
      {/*    id={selectedTaskToDelete}*/}
      {/*  />*/}
      {/*)}*/}
      {isOpenDeleteModal === true && (
        <DeleteModal
          isModalOpen={isOpenDeleteModal}
          closeModal={() => toggleDeleteModal(selectedTaskToDelete)}
          deleteTask={() => deleteTask(selectedTaskToDelete)}
        />
      )}
    </div>
  );
};

export default UserProfileRecipesListContainer;
