import React, { useEffect, useState } from "react";
import axios from "axios";
import { Modal } from "react-bootstrap";
import ModalHeader from "react-bootstrap/ModalHeader";
import "./EditRecipeStyle.css";
import moment from "moment";
import RecipeImageUploader from "../recipeImageUploader/RecipeImageUploader";
import TextEditor from "../../tasks/textEditor/TextEditor";
import UserLayout from "../../../packages/userLayout/UserLayout";
import OrangeButton from "../../../packages/buttons/orangeButton/OrangeButton";

const RecipeEditContainer = (props) => {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");
  const [file, setFile] = useState("");
  const [image, setImage] = useState("");
  const [categories, setCategories] = useState([]);
  const [categoryId, setCategoryId] = useState("");
  const [categoryName, setCategoryName] = useState("");
  const [categoriesLoading, setCategoriesLoading] = useState(true);
  const [isLoading, setIsLoading] = useState(true);
  const [categoryLoading, setCategoryLoading] = useState(true);

  useEffect(() => {
    fetchRecipe();
    fetchCategories();
    getCategoryByTitle();
  }, []);

  const fetchRecipe = () => {
    setIsLoading(true);
    axios
      .get(`http://localhost:4000/tasks/${props.id}`)
      .then((res) => {
        setTitle(res.data[0].title);
        setDate(moment.unix(res.data[0].Date).format("DD.MM.YYYY "));
        setDescription(res.data[0].description);
        setImage(res.data[0].image);
        setCategoryId(res.data[0].categoryId);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };
  const fetchCategories = () => {
    setCategoriesLoading(true);
    axios
      .get(`http://localhost:4000/category`)
      .then((res) => {
        setCategories(res.data);
        setCategoriesLoading(false);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };
  const getCategoryByTitle = () => {
    setCategoryLoading(true);
    axios
      .get(`http://localhost:4000/category/${categoryId}`)
      .then((res) => {
        setCategoryName(res.data[0].name);
        setCategoryLoading(false);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };
  const handleSubmit = () => {
    let recipe = {
      title: title,
      Date: moment(date).unix(),
      description: description,
      image: image,
      categoryId: categoryId,
    };

    axios
      .put(`http://localhost:4000/tasks/${props.id}`, { recipe })
      .then((res) => {
        window.location.reload();
      })
      .catch((err) => {
        console.log(err.response);
      });
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };
  const handleDateChange = (e) => {
    setDate(e.target.value);
  };
  const handleImageChange = (e) => {
    e.preventDefault();

    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      setFile(file);
      setImage(reader.result);
    };

    reader.readAsDataURL(file);
  };
  const handleQuillChange = (value) => {
    setDescription(value);
  };
  const handleSelect = (e) => {
    setCategoryId(e.target.value);
  };
  return (
    <div className="page_wrapper">
      <UserLayout>
        {" "}
        <h2>Редактирай рецепта с име: {title}</h2>
        <div>
          <label>Title</label>{" "}
          <RecipeImageUploader
            handleImageChange={handleImageChange}
            imagePreviewUrl={image}
          />
          <input
            onChange={handleTitleChange}
            name="title"
            type="text"
            value={title}
          />
          <label>Date</label>{" "}
          <input
            onChange={handleDateChange}
            name="date"
            type="text"
            value={date}
          />
          <label>Description</label>
          <TextEditor
            placeholder={"Започнете да пишете..."}
            label={"Enter description"}
            value={description}
            onTextEditorChange={(text) => handleQuillChange(text)}
          />{" "}
          <select onChange={handleSelect} value={categoryId}>
            <option value=""> Choose category</option>
            {categories.map((category, index) => {
              return (
                <option key={index} value={category.id}>
                  {category.name}
                </option>
              );
            })}
          </select>
          <OrangeButton onClick={handleSubmit} text={"Редактирай"} />
        </div>
      </UserLayout>
    </div>
  );
};

export default RecipeEditContainer;
